      public final String SQL_TEMPLATE_TYPE_UPDATE = UPDATE + TABLE_TEMPLATE_CATEGORY + SET + SETS(TEMPLATE_TYPE_ALL_FIELDS)+ WHERE + TEMPLATE_CATEGORY_ID + EQUAL + QUESTION;
