      public final String SQL_TEMPLATE_TYPE_CHECK_CODE_ID = SELECT + STAR + FROM + TABLE_TEMPLATE_CATEGORY + WHERE + TEMPLATE_CATEGORY_CODE + EQUAL + QUESTION + AND + TEMPLATE_CATEGORY_ID + DIFF + QUESTION;   
